import React, { useEffect, useState } from "react";
import api from "../api";

export default function AdminStore() {
  const [cfg, setCfg] = useState(null);

  useEffect(() => {
    load();
  }, []);

  async function load() {
    const r = await api.get("/store-config");
    setCfg(r.data);
  }

  async function save() {
    await api.put(`/store-config/${cfg.id}`, cfg);
    alert("Configurações salvas!");
  }

  if (!cfg) return <div className="container">Carregando...</div>;

  return (
    <div className="container" style={{ maxWidth: 700, paddingTop: 30 }}>
      <h2>Configurações da Loja</h2>
      <hr />

      {/* Nome da loja */}
      <label>Nome da Loja</label>
      <input
        value={cfg.storeName || ""}
        onChange={(e) => setCfg({ ...cfg, storeName: e.target.value })}
      />

      {/* Subtítulo */}
      <label>Subtítulo da Loja</label>
      <input
        value={cfg.storeSubtitle || ""}
        onChange={(e) => setCfg({ ...cfg, storeSubtitle: e.target.value })}
      />

      {/* Logo */}
      <label>Logo (URL ou nome de arquivo em /images)</label>
      <input
        value={cfg.logoUrl || ""}
        onChange={(e) => setCfg({ ...cfg, logoUrl: e.target.value })}
      />

      {/* Cor do tema */}
      <label>Cor do Tema</label>
      <input
        type="color"
        value={cfg.themeColor || "#2c2b6e"}
        onChange={(e) => setCfg({ ...cfg, themeColor: e.target.value })}
      />

      {/* Fundo personalizado */}
      <label>Imagem de fundo da loja (URL ou /images/bg.jpg)</label>
      <input
        value={cfg.backgroundImage || ""}
        onChange={(e) =>
          setCfg({ ...cfg, backgroundImage: e.target.value })
        }
        placeholder="Ex: /images/wallpaper.png"
      />

<label>Tema da Loja</label>
<select
  value={cfg.themeMode || "light"}
  onChange={(e) => setCfg({ ...cfg, themeMode: e.target.value })}
>
  <option value="light">Tema Claro (Branco)</option>
  <option value="dark">Tema Escuro</option>
  <option value="glass">Semi-transparente (Glass)</option>
</select>

      {/* Pedido mínimo */}
      <label>Pedido Mínimo</label>
      <input
        type="number"
        value={cfg.minimumOrderValue ?? 0}
        onChange={(e) =>
          setCfg({
            ...cfg,
            minimumOrderValue: parseFloat(e.target.value),
          })
        }
      />

      {/* Endereço */}
      <label>Endereço (exibido em Fale Conosco)</label>
      <textarea
        value={cfg.addressText || ""}
        onChange={(e) => setCfg({ ...cfg, addressText: e.target.value })}
      />

      {/* Status da loja */}
      <label>Status da Loja</label>
      <select
        value={cfg.storeOpen}
        onChange={(e) =>
          setCfg({ ...cfg, storeOpen: e.target.value === "true" })
        }
      >
        <option value="true">Aberto</option>
        <option value="false">Fechado</option>
      </select>

      <hr style={{ margin: "25px 0" }} />

      {/* WhatsApp */}
      <h3>Contato / WhatsApp</h3>

      <label>WhatsApp principal (somente números, ex: 5511999999999)</label>
      <input
        value={cfg.whatsappNumber || ""}
        onChange={(e) => setCfg({ ...cfg, whatsappNumber: e.target.value })}
      />

      <label>WhatsApp secundário (opcional)</label>
      <input
        value={cfg.whatsappNumber2 || ""}
        onChange={(e) => setCfg({ ...cfg, whatsappNumber2: e.target.value })}
      />

      {/* Email */}
      <label>Email de contato</label>
      <input
        type="email"
        value={cfg.contactEmail || ""}
        onChange={(e) => setCfg({ ...cfg, contactEmail: e.target.value })}
      />

      {/* Texto Fale Conosco */}
      <label>Texto da página Fale Conosco</label>
      <textarea
        rows={6}
        value={cfg.contactText || ""}
        onChange={(e) => setCfg({ ...cfg, contactText: e.target.value })}
        placeholder={
          "Exemplo:\nSegunda à sábado: 08:00 às 22:00\nDomingo: Fechado\n\n" +
          "Qualquer dúvida, fale conosco."
        }
      />

      <br />
      <br />
      <button onClick={save}>Salvar Alterações</button>

      <hr style={{ margin: "25px 0" }} />

      <div>
        <strong>Fretes / Cidades:</strong>{" "}
        <a href="/painel-fretes">Gerenciar regiões de entrega</a>
      </div>
    </div>
  );
}
