import React, { useEffect, useState } from "react";
import api from "../api";

export default function Checkout() {
  const [items, setItems] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const [delivery, setDelivery] = useState(0);
  const [total, setTotal] = useState(0);

  const [form, setForm] = useState({
    name: "",
    phone: "",
    region: "",
    cep: "",
    street: "",
    number: "",
    complement: "",
    reference: "",
    paymentMethod: "Dinheiro",
    change: ""
  });

  useEffect(() => {
    load();
  }, []);

  async function load() {
    const sessionId = localStorage.getItem("sessionId");
    const r = await api.get("/cart/" + sessionId);
    setItems(r.data);

    const st = r.data.reduce((acc, i) => acc + i.price * i.quantity, 0);
    setSubtotal(st);
    setTotal(st + delivery);
  }

  function calcDelivery(region) {
    const fees = {
      Jandira: 10,
      Barueri: 20,
      Itapevi: 20,
      Cotia: 20
    };
    const fee = fees[region] || 0;
    setDelivery(fee);
    setTotal(subtotal + fee);
  }

  async function submitOrder() {
    const sessionId = localStorage.getItem("sessionId");

    const req = {
      sessionId,
      ...form
    };

    try {
      const r = await api.post("/orders/checkout", req);
      const order = r.data;

      // -------------------------------
      // üìå Montar endere√ßo completo
      // -------------------------------
      let enderecoCompleto =
        `${form.street}, ${form.number}` +
        (form.complement ? ` ‚Äì ${form.complement}` : "") +
        `, ${form.region}, CEP: ${form.cep}` +
        (form.reference ? ` (Ref: ${form.reference})` : "");

      // -------------------------------
      // üìå Criar nota formatada
      // -------------------------------
      let msg =
        `*DEEPSURVEY SUPLEMENTOS*\n` +
        `------------------------------\n` +
        `*Pedido #${order.id}*\n` +
        `------------------------------\n\n` +
        `*Itens do pedido:*\n`;

      order.items.forEach(i => {
        msg += `‚Ä¢ ${i.quantity}x ${i.product.name} ‚Äì R$ ${i.price.toFixed(2).replace(".", ",")}\n`;
      });

      msg +=
        `\n------------------------------\n` +
        `Subtotal: R$ ${subtotal.toFixed(2).replace(".", ",")}\n` +
        `Entrega: R$ ${delivery.toFixed(2).replace(".", ",")}\n` +
        `*Total: R$ ${total.toFixed(2).replace(".", ",")}*\n` +
        `------------------------------\n\n` +
        `*Endere√ßo Completo:*\n${enderecoCompleto}\n\n` +
        `Forma de pagamento: ${form.paymentMethod}\n\n` +
        `https://deepsurveysuplementos.com.br`;

      const phone = "5511947935371"; // seu n√∫mero
      const url = `https://wa.me/${phone}?text=${encodeURIComponent(msg)}`;

      window.location.href = url;

    } catch (e) {
      alert("Erro ao enviar pedido. Veja o console.");
      console.error(e);
    }
  }

  return (
    <div className="container" style={{ maxWidth: 900, paddingTop: 30 }}>
      <h2>Pedido Delivery</h2>

      <div style={{ display: "flex", gap: 30 }}>

        {/* FORMUL√ÅRIO */}
        <div style={{ flex: 1 }}>
          <h4>Dados do cliente</h4>

          <input className="form-control" placeholder="Nome completo"
            onChange={e => setForm({ ...form, name: e.target.value })} />

          <input className="form-control" placeholder="WhatsApp"
            onChange={e => setForm({ ...form, phone: e.target.value })} />

          <h4 style={{ marginTop: 20 }}>Entrega</h4>

          <select className="form-control"
            onChange={e => {
              setForm({ ...form, region: e.target.value });
              calcDelivery(e.target.value);
            }}>
            <option value="">Selecione...</option>
            <option>Jandira</option>
            <option>Barueri</option>
            <option>Itapevi</option>
            <option>Cotia</option>
          </select>

          <input className="form-control" placeholder="CEP"
            onChange={e => setForm({ ...form, cep: e.target.value })} />

          <input className="form-control" placeholder="Rua"
            onChange={e => setForm({ ...form, street: e.target.value })} />

          <input className="form-control" placeholder="N√∫mero"
            onChange={e => setForm({ ...form, number: e.target.value })} />

          <input className="form-control" placeholder="Complemento"
            onChange={e => setForm({ ...form, complement: e.target.value })} />

          <input className="form-control" placeholder="Ponto de refer√™ncia"
            onChange={e => setForm({ ...form, reference: e.target.value })} />

          <h4 style={{ marginTop: 20 }}>Pagamento</h4>

          <select className="form-control"
            onChange={e => setForm({ ...form, paymentMethod: e.target.value })}>
            <option>Dinheiro</option>
            <option>Cart√£o de D√©bito</option>
            <option>Cart√£o de Cr√©dito</option>
            <option>Pix</option>
          </select>

          <input className="form-control" placeholder="Troco (opcional)"
            onChange={e => setForm({ ...form, change: e.target.value })} />

          <button className="btn btn-success mt-3" onClick={submitOrder}>
            Enviar pedido via WhatsApp
          </button>
        </div>

        {/* RESUMO */}
        <div style={{
          width: 300,
          background: "#fff8d5",
          padding: 20,
          borderRadius: 12
        }}>
          <h4>Resumo do pedido</h4>
          <p><b>Subtotal:</b> R$ {subtotal.toFixed(2)}</p>
          <p><b>Entrega:</b> R$ {delivery.toFixed(2)}</p>
          <p><b>Total:</b> R$ {total.toFixed(2)}</p>
        </div>
      </div>
    </div>
  );
}
