  const [total, setTotal] = useState(0);

  const [regions, setRegions] = useState([]);
  const [loading, setLoading] = useState(false);

  const { cfg } = useStoreConfig();

  const [form, setForm] = useState({
    name: "",
    phone: "",
    city: "",
    cep: "",
    street: "",
    number: "",
    complement: "",
    reference: "",
    paymentMethod: "Dinheiro",
    change: "",
  });

  const [errors, setErrors] = useState({});

  useEffect(() => {
    loadCart();
    loadRegions();
  }, []);

  // Carrega carrinho
  async function loadCart() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) return;

    try {
      const r = await api.get("/cart/" + sessionId);
      const data = r.data || [];
      setItems(data);

      const st = data.reduce(
        (acc, i) => acc + (i.price || 0) * (i.quantity || 0),
        0
      );

      setSubtotal(st);
      setTotal(st);
    } catch (err) {
      console.error("Erro ao carregar carrinho", err);
    }
  }

  // Carrega regi√µes de entrega
  async function loadRegions() {
    try {
      const r = await api.get("/delivery-regions");
      setRegions(r.data.filter((rg) => rg.active));
    } catch (err) {
      console.error("Erro ao carregar regi√µes de entrega", err);
    }
  }

  // Calcula frete com base na cidade
  function calcDelivery(cityName) {
    const region = regions.find((r) => r.name === cityName);
    const valor = region ? region.fee : 0;
    setDelivery(valor);
    setTotal(subtotal + valor);
  }

  // Busca CEP no ViaCEP e preenche rua
  async function buscarCEP() {
    const cepNum = form.cep.replace(/\D/g, "");
    if (cepNum.length !== 8) return;

    try {
      const r = await fetch(`https://viacep.com.br/ws/${cepNum}/json/`);
      const data = await r.json();
      if (!data.erro) {
        setForm((prev) => ({
          ...prev,
          street: data.logradouro || prev.street,
        }));
      }
    } catch (err) {
      console.error("Erro ao buscar CEP:", err);
    }
  }

  // VALIDA√á√ÉO DO FORMUL√ÅRIO
  function validateForm() {
    const newErrors = {};

    if (!form.name.trim()) newErrors.name = "Informe seu nome completo.";

    if (!form.phone.trim() || form.phone.length < 10)
      newErrors.phone = "Informe um Whatsapp v√°lido.";

    if (!form.city.trim()) newErrors.city = "Selecione sua cidade.";

    if (!form.cep.trim() || form.cep.replace(/\D/g, "").length < 8)
      newErrors.cep = "CEP inv√°lido.";

    if (!form.street.trim()) newErrors.street = "Informe a rua.";

    if (!form.number.trim()) newErrors.number = "Informe o n√∫mero.";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }

  function fmt(v) {
    return Number(v || 0).toFixed(2).replace(".", ",");
  }

  // ENVIO DO PEDIDO
  async function submitOrder() {
    // evita duplo clique
    if (loading) return;

    // loja fechada
    if (cfg && cfg.storeOpen === false) {
      alert("A loja est√° fechada no momento. N√£o √© poss√≠vel enviar pedidos.");
      return;
    }

    // sem regi√µes de entrega configuradas
    if (!regions || regions.length === 0) {
      alert("No momento n√£o h√° regi√µes de entrega configuradas.");
      return;
    }

    // sem itens
    if (!items || items.length === 0) {
      alert("Seu carrinho est√° vazio.");
      return;
    }

    if (!validateForm()) {
      alert("Preencha todos os campos obrigat√≥rios.");
      return;
    }

    if (!form.city) {
      alert("Selecione sua cidade para calcular o frete.");
      return;
    }

    const sessionId = localStorage.getItem("sessionId");

    // trata troco
    let changeValue = form.change || null;
    if (changeValue !== null && changeValue !== "") {
      // troca v√≠rgula por ponto
      changeValue = Number(
        String(changeValue).replace(",", ".").replace(" ", "")
      );

      if (isNaN(changeValue)) {
        alert("Valor de troco inv√°lido.");
        return;
      }

      // se troco menor que total ‚Üí erro
      if (changeValue < total) {
        alert(
          `O valor para troco (R$ ${fmt(
            changeValue
          )}) √© menor que o total do pedido (R$ ${fmt(total)}).`
        );
        return;
      }
    } else {
      changeValue = null;
    }

    const req = {
      sessionId,
      name: form.name,
      phone: form.phone,
      region: form.city,
      cep: form.cep,
      street: form.street,
      number: form.number,
      complement: form.complement,
      reference: form.reference,
      paymentMethod: form.paymentMethod,
      change: changeValue,
    };

    try {
      setLoading(true);

      const r = await api.post("/orders/checkout", req);
      const order = r.data;

      localStorage.removeItem("sessionId");

      // MONTA A MENSAGEM DO WHATSAPP
      let msg =
        `DEEPSURVEY SUPLEMENTOS\n` +
        `------------------------------\n\n` +
        `Pedido #${order.id}\n\n` +
        `Itens:\n`;

      order.items.forEach((i) => {
        msg += `${i.quantity}x ${i.product.name} - R$ ${Number(i.price)
          .toFixed(2)
          .replace(".", ",")}\n`;
      });

      msg += `\nSubtotal: R$ ${subtotal.toFixed(2).replace(".", ",")}\n`;
      msg += `Entrega: R$ ${delivery.toFixed(2).replace(".", ",")}\n`;
      msg += `Total: R$ ${total.toFixed(2).replace(".", ",")}\n\n`;

      msg += `Endere√ßo:\n`;
      msg += `${form.street} ${form.number}`;
      if (form.complement) msg += ` - ${form.complement}`;
      msg += `, ${form.city}`;
      msg += `, CEP: ${form.cep}`;
      if (form.reference) msg += ` (Ref: ${form.reference})`;
      msg += `\n\n`;

      msg += `Pagamento: ${form.paymentMethod}\n`;
      if (form.change)
        msg += `Troco para R$ ${form.change}\n\n`;
      else
        msg += `\n`;

      msg += `www.deepsurveysuplementos.com.br`;

      // tenta copiar mensagem pro clipboard (opcional)
      try {
        if (navigator.clipboard && navigator.clipboard.writeText) {
          await navigator.clipboard.writeText(msg);
        }
      } catch (e) {
        console.warn("N√£o foi poss√≠vel copiar para a √°rea de transfer√™ncia:", e);
      }

      // codifica mensagem
      const encoded = encodeURIComponent(msg);

      const whatsappNumber = cfg?.whatsappNumber || "5511947935371";

      // abre WhatsApp
      window.location.href =
        `https://api.whatsapp.com/send?phone=${whatsappNumber}&text=${encoded}`;
    } catch (err) {
      console.error("Erro no checkout:", err);
      alert("Erro ao enviar pedido. Tente novamente.");
    } finally {
      setLoading(false);
    }
  }

  // EXIBI√á√ÉO DO FORMUL√ÅRIO
  return (
    <div className="container" style={{ maxWidth: 1100, paddingTop: 30 }}>
      <h2>Pedido Delivery</h2>

      <div style={{ display: "flex", gap: 40, alignItems: "flex-start" }}>
        <div style={{ flex: 1 }}>
          <div className="section-title">üßç Dados do cliente</div>

          {/* Nome */}
          <input
            className={`form-input ${errors.name ? "error" : ""}`}
            placeholder="Nome completo"
            value={form.name}
            onChange={(e) =>
              setForm({
                ...form,
                name: e.target.value.replace(/[^A-Za-z√Ä-√ñ√ò-√∂√∏-√ø\s\.'-]/g, ""),
              })
            }
          />
          {errors.name && <div className="input-error">{errors.name}</div>}

          {/* Whatsapp */}
          <input
            className={`form-input ${errors.phone ? "error" : ""}`}
            placeholder="Whatsapp"
            value={form.phone}
            onChange={(e) =>
              setForm({
                ...form,
                phone: e.target.value.replace(/\D/g, ""),
              })
            }
          />
          {errors.phone && <div className="input-error">{errors.phone}</div>}

          <div className="section-title" style={{ marginTop: 25 }}>
            üì¶ Entrega
          </div>

          {/* Cidade */}
          <label>Cidade:</label>
          <select
            className={`form-input ${errors.city ? "error" : ""}`}
            value={form.city}
            onChange={(e) => {
              const value = e.target.value;
              setForm({ ...form, city: value });
              calcDelivery(value);
            }}
          >
            <option value="">Selecione...</option>
            {regions.map((r) => (
              <option key={r.id} value={r.name}>
                {r.name}
              </option>
            ))}
          </select>
          {errors.city && <div className="input-error">{errors.city}</div>}

          {/* CEP */}
          <input
            className={`form-input ${errors.cep ? "error" : ""}`}
            placeholder="CEP"
            value={form.cep}
            onChange={(e) =>
              setForm({
                ...form,
                cep: e.target.value.replace(/[^0-9-]/g, ""),
              })
            }
            onBlur={buscarCEP}
          />
          {errors.cep && <div className="input-error">{errors.cep}</div>}

          {/* Rua */}
          <input
            className={`form-input ${errors.street ? "error" : ""}`}
            placeholder="Rua"
            value={form.street}
            onChange={(e) =>
              setForm({
                ...form,
                street: e.target.value.replace(
                  /[^A-Za-z√Ä-√ñ√ò-√∂√∏-√ø0-9\s\.,'-]/g,
                  ""
                ),
              })
            }
          />
          {errors.street && <div className="input-error">{errors.street}</div>}

          {/* N√∫mero */}
          <input
            className={`form-input ${errors.number ? "error" : ""}`}
            placeholder="N√∫mero"
            value={form.number}
            onChange={(e) =>
              setForm({
                ...form,
                number: e.target.value.replace(/[^0-9A-Za-z\-\/]/g, ""),
              })
            }
          />
          {errors.number && <div className="input-error">{errors.number}</div>}

          {/* Complemento */}
          <input
            className="form-input"
            placeholder="Complemento"
            value={form.complement}
            onChange={(e) => setForm({ ...form, complement: e.target.value })}
          />

          {/* Refer√™ncia */}
          <input
            className="form-input"
            placeholder="Ponto de refer√™ncia"
            value={form.reference}
            onChange={(e) =>
              setForm({
                ...form,
                reference: e.target.value.replace(
                  /[^A-Za-z√Ä-√ñ√ò-√∂√∏-√ø0-9\s\.,'-]/g,
                  ""
                ),
              })
            }
          />

          <div className="section-title" style={{ marginTop: 25 }}>
            üí≥ Pagamento
          </div>

          {/* M√©todo de pagamento */}
          <select
            className="form-input"
            value={form.paymentMethod}
            onChange={(e) =>
              setForm({ ...form, paymentMethod: e.target.value })
            }
          >
            <option>Dinheiro</option>
            <option>D√©bito</option>
            <option>Cr√©dito</option>
            <option>Pix</option>
          </select>

          {/* Troco */}
          <input
            className="form-input"
            placeholder="Troco (opcional)"
            value={form.change}
            onChange={(e) =>
              setForm({
                ...form,
                change: e.target.value.replace(/[^0-9,\.]/g, ""),
              })
            }
          />

          <button
            className="btn-confirm"
            onClick={submitOrder}
            disabled={loading}
          >
            {loading ? "Enviando..." : "Enviar pedido via WhatsApp"}
          </button>
        </div>

        {/* RESUMO */}
        <div style={{ width: 320 }}>
          <div
            style={{
              background: "#fff8d5",
              padding: 18,
              borderRadius: 6,
              boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
              border: "1px solid rgba(0,0,0,0.06)",
            }}
          >
            <div style={{ textAlign: "center", fontWeight: 700 }}>
  {cfg?.storeName || "Minha Loja"}
  <div style={{ fontWeight: 400, fontSize: 13 }}>
    {cfg?.storeSubtitle}
  </div>
</div>
            <div
              style={{
                textAlign: "center",
                color: "#777",
                margin: "8px 0 12px",
              }}
            >
              <span
                style={{
                  display: "inline-block",
                  width: 80,
                  borderTop: "2px solid rgba(0,0,0,0.15)",
                }}
              />
            </div>

            <div
              style={{
                textAlign: "center",
                fontWeight: 700,
                marginBottom: 6,
              }}
            >
              Pedido
            </div>

            <div style={{ height: 8 }} />

            <div
              style={{
                borderTop: "1px dashed rgba(0,0,0,0.12)",
                paddingTop: 10,
              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  marginBottom: 8,
                }}
              >
                <div style={{ color: "#333" }}>Subtotal:</div>
                <div style={{ fontWeight: 700 }}>R$ {fmt(subtotal)}</div>
              </div>

              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                }}
              >
                <div style={{ color: "#333" }}>Entrega:</div>
                <div style={{ fontWeight: 700 }}>
                  {delivery > 0 ? `R$ ${fmt(delivery)}` : "‚Äî"}
                </div>
              </div>

              <div
                style={{
                  borderTop: "1px solid rgba(0,0,0,0.06)",
                  marginTop: 10,
                  paddingTop: 10,
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                  }}
                >
                  <div style={{ fontWeight: 800 }}>Total:</div>
                  <div style={{ fontWeight: 800 }}>R$ {fmt(total)}</div>
                </div>
              </div>
            </div>

            <div
              style={{
                marginTop: 14,
                textAlign: "center",
                color: "#333",
                fontSize: 13,
              }}
            >
              <div style={{ fontWeight: 700, marginBottom: 6 }}>
                {form.paymentMethod}
              </div>
            </div>
          </div>

          <div style={{ textAlign: "center", color: "#888", marginTop: 12 }}>
            O seu pedido ser√° enviado para o nosso WhatsApp
          </div>
        </div>
      </div>
    </div>
  );
}
