import React from "react";
import { Link } from "react-router-dom";

/**
 * p: objeto produto (id, name, price, salePrice, onSale, imageUrl, images)
 */
export default function ProductCard({ p }) {
  // calcula imagem principal com fallback
  const mainImg = (() => {
    // p.imageUrl pode ser nulo, array, ou string relativa/absoluta
    if (!p) return "/images/placeholder.png";
    if (p.imageUrl) {
      if (Array.isArray(p.imageUrl) && p.imageUrl.length) {
        return p.imageUrl[0].startsWith("http") ? p.imageUrl[0] : `/images/${p.imageUrl[0]}`;
      }
      if (typeof p.imageUrl === "string" && p.imageUrl.trim() !== "") {
        return p.imageUrl.startsWith("http") ? p.imageUrl : `/images/${p.imageUrl}`;
      }
    }
    // fallback para campo images
    if (Array.isArray(p.images) && p.images.length) {
      const img = p.images[0];
      return img.startsWith("http") ? img : `/images/${img}`;
    }
    // placeholder local (adicione /images/placeholder.png no seu public/images)
    return "/images/placeholder.png";
  })();

  const displayPrice = p.onSale && p.salePrice ? p.salePrice : p.price;

  return (
    <Link
      to={`/produto/${p.id}`}
      className="card product-card"
      style={{ textDecoration: "none", color: "inherit" }}
      aria-label={p.name}
    >
      <div className="img-wrap">
        <img
          src={mainImg}
          alt={p.name}
          onError={(e) => {
            e.currentTarget.onerror = null;
            e.currentTarget.src = "/images/placeholder.png";
          }}
          className="product-thumb"
        />
        {p.onSale && (
          <div className="badge">Oferta</div>
        )}
      </div>

      <h3 className="product-title">{p.name}</h3>

      <div className="price-row">
        {p.onSale && p.salePrice ? (
          <>
            <div className="price price-sale">R$ {Number(p.salePrice).toFixed(2).replace(".", ",")}</div>
            <div className="old-price">R$ {Number(p.price).toFixed(2).replace(".", ",")}</div>
          </>
        ) : (
          <div className="price">R$ {Number(p.price).toFixed(2).replace(".", ",")}</div>
        )}
      </div>
    </Link>
  );
}
