import React from "react";
import { useStoreConfig } from "../context/StoreConfigContext";

export default function FaleConosco() {
  const { cfg } = useStoreConfig();

  if (!cfg) {
    return (
      <div className="contact-card box">
        Carregando...
      </div>
    );
  }

  const phoneMain = cfg.whatsappNumber || "5511947935371";
  const phone2 = cfg.whatsappNumber2 || null;

  const whatsappUrl = (num) =>
    `https://api.whatsapp.com/send?phone=${num}&text=${encodeURIComponent(
      "Ol√°! Gostaria de pedir informa√ß√µes."
    )}`;

  const renderMultiline = (text) =>
    (text || "")
      .split("\n")
      .filter((line) => line.trim() !== "")
      .map((line, idx) => <p key={idx}>{line}</p>);

  return (
    <div className="container" style={{ maxWidth: 700, paddingTop: 40 }}>
      <h2>Fale Conosco</h2>
      <p style={{ marginBottom: 25, color: "#444" }}>
        Entre em contato conosco pelos canais abaixo. Estamos dispon√≠veis para
        tirar d√∫vidas, receber pedidos e ajudar no que for preciso.
      </p>

      {/* Funcionamento / Texto livre */}
      <div
        style={{
          padding: 20,
          borderRadius: 10,
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
          marginBottom: 25,
        }}
      >
        <h4>üìÖ Funcionamento / Informa√ß√µes</h4>
        {renderMultiline(cfg.contactText)}
      </div>

      {/* Endere√ßo */}
      <div
        style={{
          padding: 20,
          borderRadius: 10,
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
          marginBottom: 25,
        }}
      >
        <h4>üìç Endere√ßo</h4>
        {renderMultiline(cfg.addressText)}
      </div>

      {/* Contato */}
      <div
        style={{
          padding: 20,
          borderRadius: 10,
          background: "#fff",
          boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
        }}
      >
        <h4>üìû Contato</h4>

        <p>
          üì± WhatsApp:{" "}
          <a
            href={whatsappUrl(phoneMain)}
            target="_blank"
            rel="noreferrer"
            style={{ color: "#128C7E", fontWeight: 600 }}
          >
            {phoneMain}
          </a>
        </p>

        {phone2 && (
          <p>
            üì± WhatsApp 2:{" "}
            <a
              href={whatsappUrl(phone2)}
              target="_blank"
              rel="noreferrer"
              style={{ color: "#128C7E", fontWeight: 600 }}
            >
              {phone2}
            </a>
          </p>
        )}

        {cfg.contactEmail && (
          <p>
            ‚úâÔ∏è E-mail:{" "}
            <a
              href={`mailto:${cfg.contactEmail}`}
              style={{ color: "#555", fontWeight: 600 }}
            >
              {cfg.contactEmail}
            </a>
          </p>
        )}
      </div>

      <div style={{ textAlign: "center", marginTop: 30 }}>
        <a
          href={whatsappUrl(phoneMain)}
          target="_blank"
          rel="noreferrer"
          style={{
            padding: "12px 20px",
            background: "#25D366",
            color: "#fff",
            borderRadius: 8,
            textDecoration: "none",
            fontWeight: 600,
            fontSize: 16,
          }}
        >
          üí¨ Falar via WhatsApp
        </a>
      </div>
    </div>
  );
}
