import React, { useEffect, useState } from "react";
import api from "../api";

// Lista de cidades, bairros e taxa de entrega
const cidades = {
  "Jandira-SP": {
    "Jardim Novo Horizonte": 10,
    "Jardim Gabriela": 10,
    "Centro": 10,
    "Jardim Ouro Verde": 10
  },
  "Barueri-SP": {
    "Centro": 20,
    "Aldeia da Serra": 25,
    "Jardim Belval": 18
  },
  "Itapevi-SP": {
    "Centro": 20,
    "Parque Suburbano": 15,
    "Vila Nova": 18
  },
  "Cotia-SP": {
    "Centro": 20,
    "Granja Viana": 25,
    "Jardim Nomura": 18
  }
};

export default function Checkout() {
  const [items, setItems] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const [delivery, setDelivery] = useState(0);
  const [total, setTotal] = useState(0);

  const [form, setForm] = useState({
    name: "",
    phone: "",
    city: "",
    district: "",
    cep: "",
    street: "",
    number: "",
    complement: "",
    reference: "",
    paymentMethod: "Dinheiro",
    change: ""
  });

  useEffect(() => {
    load();
  }, []);

  async function load() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) return;

    try {
      const r = await api.get("/cart/" + sessionId);
      const data = r.data || [];
      setItems(data);

      const st = data.reduce(
        (acc, i) => acc + (i.price || 0) * (i.quantity || 0),
        0
      );

      setSubtotal(st);
      setTotal(st + delivery);
    } catch (err) {
      console.error("Erro ao carregar carrinho", err);
    }
  }

  function handleCityChange(city) {
    setForm({ ...form, city, district: "" });

    // ao trocar cidade, zera frete
    setDelivery(0);
    setTotal(subtotal);
  }

  function handleDistrictChange(district) {
    const fee = cidades[form.city]?.[district] || 0;
    setForm({ ...form, district });

    setDelivery(fee);
    setTotal(subtotal + fee);
  }

  async function submitOrder() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      alert("Sess√£o inv√°lida.");
      return;
    }

    let changeValue = form.change;
    if (changeValue === "" || changeValue === null) {
      changeValue = null;
    } else {
      changeValue = Number(changeValue);
      if (isNaN(changeValue)) {
        alert("O campo Troco deve ser um n√∫mero.");
        return;
      }
