import React, { useEffect, useState } from "react";
import api from "../api";

function fmt(v) {
  return Number(v || 0).toFixed(2).replace(".", ",");
}

export default function Checkout() {
  const [items, setItems] = useState([]);
  const [loadingItems, setLoadingItems] = useState(true);

  // cliente / entrega
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [cep, setCep] = useState("");
  const [street, setStreet] = useState("");
  const [number, setNumber] = useState("");
  const [complement, setComplement] = useState("");
  const [reference, setReference] = useState("");

  // pagamento
  const [paymentMethod, setPaymentMethod] = useState("Dinheiro");
  const [cardBrand, setCardBrand] = useState("");

  // entrega / bairros - você pode ajustar essa lista / valores
  const [bairro, setBairro] = useState("Barueri - SP");
  const [deliveryFee, setDeliveryFee] = useState(20.0); // valor editável

  // cupom (apenas UI, não aplica desconto automático aqui)
  const [coupon, setCoupon] = useState("");

  // loja / whatsApp (você informou)
  const storeName = "DeepSurvey Suplementos";
  const storePhone = "5511947935371"; // sem + e sem espaços pro link

  useEffect(() => {
    async function loadCart() {
      setLoadingItems(true);
      const sessionId = localStorage.getItem("sessionId");
      if (!sessionId) {
        setItems([]);
        setLoadingItems(false);
        return;
      }
      try {
        const r = await api.get("/cart/" + sessionId);
        setItems(r.data || []);
      } catch (err) {
        console.error("erro ao buscar carrinho", err);
        setItems([]);
      } finally {
        setLoadingItems(false);
      }
    }
    loadCart();
  }, []);

  const subtotal = items.reduce((s, it) => s + (it.price || it.product?.price || 0) * (it.quantity || 1), 0);
  const total = subtotal + Number(deliveryFee || 0);

  function buildAddressString() {
    // junta campos do endereço para enviar ao backend (campo address do CheckoutRequest)
    return `${cep ? `CEP: ${cep}, ` : ""}Rua: ${street || ""}, Nº: ${number || ""}${complement ? `, Complemento: ${complement}` : ""}${reference ? `, Ref: ${reference}` : ""}, Bairro: ${bairro}`;
  }

  async function sendOrder() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      alert("Carrinho vazio (sessionId ausente). Adicione produtos antes de finalizar.");
      return;
    }
    if (!name || !phone || !street) {
      alert("Preencha pelo menos nome, telefone e rua.");
      return;
    }

    // montar payload para o backend
    const payload = {
      sessionId,
      name,
      phone,
      address: buildAddressString()
    };

    try {
      // 1) chamar o endpoint do backend que cria o pedido
      const res = await api.post("/orders/checkout", payload);

      // se chegou aqui, pedido criado e backend limpou o carrinho (deleteBySessionId)
      const order = res.data;

      // 2) construir texto da nota (estilo da sua imagem)
      let lines = [];
      lines.push(`*${storeName}*`);
      lines.push("------");
      lines.push(`*Pedido* ${order.id ? `#${order.id}` : ""}`);
      lines.push("------");
      lines.push(new Date().toLocaleString());
      lines.push("------");
      lines.push(`*Nome:* ${name}`);
      lines.push(`*WhatsApp:* ${phone}`);
      lines.push(`*Endereço:* ${buildAddressString()}`);
      lines.push("------");
      lines.push("*PRODUTOS*");
      items.forEach((it, idx) => {
        const qty = it.quantity || 1;
        const title = it.product?.name || "Produto";
        const price = (it.price != null ? it.price : it.product?.price) || 0;
        lines.push(`${qty} x ${title} - Valor: R$ ${fmt(price)}`);
      });
      lines.push("------");
      lines.push(`Subtotal: R$ ${fmt(subtotal)}`);
      lines.push(`Entrega: R$ ${fmt(deliveryFee)}`);
      lines.push(`Forma de pagamento: ${paymentMethod}${cardBrand ? " - Bandeira: " + cardBrand : ""}`);
      lines.push(`Total: R$ ${fmt(total)}`);
      lines.push("------");
      lines.push("https://"+ "www.deepsurveysuplementos.com.br"); // seu domínio hipotético

      const text = lines.join("\n");

      // 3) abrir WhatsApp com a mensagem (abre web ou app dependendo do client)
      const encoded = encodeURIComponent(text);
      const waUrl = `https://api.whatsapp.com/send?phone=${storePhone}&text=${encoded}`;
      window.open(waUrl, "_blank");

      // 4) limpar sessionId local (backend já limpou os itens)
      localStorage.removeItem("sessionId");
      setItems([]);

      alert("Pedido enviado. Você será direcionado ao WhatsApp.");

    } catch (err) {
      console.error("erro ao enviar pedido:", err);
      let msg = "Erro ao enviar pedido. Confira o console.";
      if (err.response && err.response.data) msg += `\n${JSON.stringify(err.response.data)}`;
      alert(msg);
    }
  }

  if (loadingItems) return <div className="container" style={{ paddingTop: 40 }}>Carregando...</div>;

  return (
    <div className="container" style={{ paddingTop: 40, display: "flex", gap: 30 }}>
      <div style={{ flex: 1 }}>
        <h2>Dados do cliente</h2>

        <label>Nome completo</label>
        <input value={name} onChange={e => setName(e.target.value)} className="form-input" />

        <label>WhatsApp (ex: 11912345678)</label>
        <input value={phone} onChange={e => setPhone(e.target.value)} className="form-input" />

        <h3 style={{ marginTop: 20 }}>Entrega</h3>

        <label>Bairro / Região</label>
        <select value={bairro} onChange={e => setBairro(e.target.value)} className="form-input">
          <option>Barueri - SP</option>
          <option>Jandira - SP</option>
          <option>Cotia - SP</option>
          <option>Outro</option>
        </select>

        <label>Taxa de entrega (editar quando precisar)</label>
        <input
          type="number"
          step="0.01"
          value={deliveryFee}
          onChange={(e) => setDeliveryFee(Number(e.target.value))}
          className="form-input"
        />

        <label>CEP</label>
        <input value={cep} onChange={e => setCep(e.target.value)} className="form-input" />

        <label>Rua</label>
        <input value={street} onChange={e => setStreet(e.target.value)} className="form-input" />

        <label>Número</label>
        <input value={number} onChange={e => setNumber(e.target.value)} className="form-input" />

        <label>Complemento</label>
        <input value={complement} onChange={e => setComplement(e.target.value)} className="form-input" />

        <label>Ponto de referência</label>
        <input value={reference} onChange={e => setReference(e.target.value)} className="form-input" />

        <h3 style={{ marginTop: 20 }}>Pagamento</h3>
        <label>Forma de pagamento</label>
        <select value={paymentMethod} onChange={e => setPaymentMethod(e.target.value)} className="form-input">
          <option>Dinheiro</option>
          <option>Pix</option>
          <option>Cartão de Débito</option>
          <option>Cartão de Crédito</option>
        </select>

        <label>Bandeira (opcional)</label>
        <input value={cardBrand} onChange={e => setCardBrand(e.target.value)} className="form-input" />

        <label>Cupom de desconto (opcional)</label>
        <input value={coupon} onChange={e => setCoupon(e.target.value)} className="form-input" />

        <div style={{ marginTop: 20 }}>
          <button
            onClick={sendOrder}
            style={{
              background: "#0a7cff",
              color: "#fff",
              padding: "12px 20px",
              borderRadius: 8,
              border: "none",
              fontWeight: 700,
              cursor: "pointer"
            }}
          >
            Enviar pedido (WhatsApp)
          </button>
        </div>
      </div>

      <aside style={{ width: 380 }}>
        <div style={{ background: "#fff9e6", padding: 16, borderRadius: 8, border: "1px solid #f0e1b5" }}>
          <h3 style={{ marginTop: 0 }}>{storeName}</h3>
          <div style={{ fontSize: 14, marginTop: 8 }}>
            <strong>Pedido</strong>
            <div style={{ marginTop: 10 }}>
              {items.length === 0 && <div>Seu carrinho está vazio</div>}
              {items.map((it) => (
                <div key={it.id} style={{ marginBottom: 8 }}>
                  <div style={{ fontWeight: 700 }}>{it.product?.name}</div>
                  <div>Qtd: {it.quantity}</div>
                  <div>Valor: R$ {fmt(it.price ?? it.product?.price)}</div>
                </div>
              ))}
            </div>

            <hr />
            <div>Subtotal: <strong>R$ {fmt(subtotal)}</strong></div>
            <div>Entrega: <strong>R$ {fmt(deliveryFee)}</strong></div>
            <div style={{ marginTop: 8 }}>Total: <strong>R$ {fmt(total)}</strong></div>
          </div>
        </div>
      </aside>
    </div>
  );
}
