import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import api from "../api";
import ProductCard from "../components/ProductCard";
import "./Search.css"; // estilos abaixo

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

export default function SearchResults() {
  const q = useQuery().get("q") || useQuery().get("search") || "";
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!q) {
      setItems([]);
      return;
    }

    async function load() {
      setLoading(true);
      setError(null);

      // tenta 1) /products/search?q=  2) /products?search=  3) /products (filtra client-side)
      const endpoints = [
        `/products/search?q=${encodeURIComponent(q)}`,
        `/products?search=${encodeURIComponent(q)}`,
        `/products`
      ];

      let data = null;
      for (const ep of endpoints) {
        try {
          const r = await api.get(ep);
          // se endpoint retornar 200 e array, usa
          if (Array.isArray(r.data)) {
            data = r.data;
            break;
          }
          // às vezes resposta vem em {items: [...]}
          if (r.data && Array.isArray(r.data.items)) {
            data = r.data.items;
            break;
          }
        } catch (err) {
          // ignora e tenta próximo endpoint
          console.warn("Search endpoint falhou:", ep, err.message || err);
        }
      }

      if (!data) {
        setError("Não foi possível buscar produtos (verifique o backend).");
        setItems([]);
        setLoading(false);
        return;
      }

      // se usamos fallback /products, filtramos client-side por nome/descrição
      if (endpoints[2] && data && !data.find(it => it.name && it.name.toLowerCase().includes(q.toLowerCase()))) {
        // provavelmente endpoint retornou tudo, filtramos:
        const filtered = data.filter((it) => {
          const name = (it.name || "").toLowerCase();
          const desc = (it.description || "").toLowerCase();
          return name.includes(q.toLowerCase()) || desc.includes(q.toLowerCase());
        });
        setItems(filtered);
      } else {
        setItems(data);
      }
      setLoading(false);
    }

    load();
  }, [q]);

  return (
    <div className="container" style={{ paddingTop: 40 }}>
      <h2>Resultados para: <span style={{ textTransform: "none" }}>{q}</span></h2>

      {loading && <p>Carregando resultados...</p>}
      {error && <p style={{ color: "salmon" }}>{error}</p>}

      {!loading && items.length === 0 && <p>Nenhum produto encontrado.</p>}

      <div className="search-grid">
        {items.map((p) => (
          <ProductCard key={p.id} p={p} />
        ))}
      </div>
    </div>
  );
}
