import React, { useEffect, useState } from "react";
import api from "../api";

export default function Checkout() {
  const [cart, setCart] = useState([]);

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");

  const [region, setRegion] = useState("");
  const [cep, setCep] = useState("");
  const [street, setStreet] = useState("");
  const [number, setNumber] = useState("");
  const [neighborhood, setNeighborhood] = useState("");
  const [complement, setComplement] = useState("");
  const [reference, setReference] = useState("");

  const [paymentMethod, setPaymentMethod] = useState("Dinheiro");
  const [change, setChange] = useState("");

  useEffect(() => {
    loadCart();
  }, []);

  async function loadCart() {
    const sessionId = localStorage.getItem("sessionId");
    const r = await api.get(`/cart/${sessionId}`);
    setCart(r.data || []);
  }

  function sendToWhatsApp() {
    const msg = `
üßæ *Novo Pedido - Delivery*

üë§ *Cliente*
‚Ä¢ Nome: ${name}
‚Ä¢ WhatsApp: ${phone}

üìç *Endere√ßo*
‚Ä¢ Bairro/Regi√£o: ${region}
‚Ä¢ CEP: ${cep}
‚Ä¢ Rua: ${street}, N¬∫ ${number}
‚Ä¢ Bairro: ${neighborhood}
‚Ä¢ Complemento: ${complement}
‚Ä¢ Refer√™ncia: ${reference}

üí∞ *Pagamento*
‚Ä¢ Forma: ${paymentMethod}
${paymentMethod === "Dinheiro" ? `‚Ä¢ Troco para: ${change}` : ""}

üõí *Itens*
${cart.map(i => `‚Ä¢ ${i.product.name} (${i.quantity} un) - R$ ${(i.price * i.quantity).toFixed(2)}`).join("\n")}

Total: R$ ${cart.reduce((t, c) => t + c.price * c.quantity, 0).toFixed(2)}
`;

    const url = "https://api.whatsapp.com/send?phone=5511947935371&text=" + encodeURIComponent(msg);
    window.open(url, "_blank");
  }

  return (
    <div style={{ maxWidth: 900, margin: "0 auto", padding: 20 }}>

      {/* Dados do cliente */}
      <h2 style={{ borderBottom: "1px solid #ddd", paddingBottom: 10 }}>
        üë§ Dados do cliente
      </h2>

      <div className="form-group">
        <label>Nome completo:</label>
        <input className="input" value={name} onChange={e => setName(e.target.value)} />
      </div>

      <div className="form-group">
        <label>WhatsApp:</label>
        <input className="input" value={phone} onChange={e => setPhone(e.target.value)} />
      </div>

      {/* Endere√ßo */}
      <h2 style={{ borderBottom: "1px solid #ddd", paddingBottom: 10, marginTop: 30 }}>
        üìç Entrega
      </h2>

      <div className="form-group">
        <label>Bairro / Regi√£o:</label>
        <select className="input" value={region} onChange={e => setRegion(e.target.value)}>
          <option value="">Selecione...</option>
          <option value="Jardim Novo Horizonte">Jardim Novo Horizonte</option>
          <option value="Jardim Gabriela">Jardim Gabriela</option>
          <option value="Centro">Centro</option>
          <option value="Jardim Ouro Verde">Jardim Ouro Verde</option>
        </select>
      </div>

      <div className="row">
        <div className="form-group col">
          <label>CEP:</label>
          <input className="input" value={cep} onChange={e => setCep(e.target.value)} />
        </div>

        <div className="form-group col">
          <label>N¬∫:</label>
          <input className="input" value={number} onChange={e => setNumber(e.target.value)} />
        </div>
      </div>

      <div className="form-group">
        <label>Rua:</label>
        <input className="input" value={street} onChange={e => setStreet(e.target.value)} />
      </div>

      <div className="form-group">
        <label>Bairro:</label>
        <input className="input" value={neighborhood} onChange={e => setNeighborhood(e.target.value)} />
      </div>

      <div className="form-group">
        <label>Complemento:</label>
        <input className="input" value={complement} onChange={e => setComplement(e.target.value)} />
      </div>

      <div className="form-group">
        <label>Ponto de refer√™ncia:</label>
        <input className="input" value={reference} onChange={e => setReference(e.target.value)} />
      </div>

      {/* Pagamento */}
      <h2 style={{ borderBottom: "1px solid #ddd", paddingBottom: 10, marginTop: 30 }}>
        üíµ Pagamento
      </h2>

      <div className="form-group">
        <label>Forma de pagamento:</label>
        <select className="input" value={paymentMethod} onChange={e => setPaymentMethod(e.target.value)}>
          <option>Dinheiro</option>
          <option>Pix</option>
          <option>Cart√£o</option>
        </select>
      </div>

      {paymentMethod === "Dinheiro" && (
        <div className="form-group">
          <label>Troco para:</label>
          <input className="input" value={change} onChange={e => setChange(e.target.value)} />
        </div>
      )}

      <button 
        onClick={sendToWhatsApp}
        style={{
          marginTop: 30,
          background: "#25D366",
          color: "#fff",
          border: 0,
          padding: "15px 20px",
          borderRadius: 10,
          cursor: "pointer",
          width: "100%",
          fontSize: 18,
          fontWeight: "bold"
        }}
      >
        Enviar pedido via WhatsApp
      </button>

      {/* Estilos r√°pidos */}
      <style>{`
        .form-group { margin-bottom: 20px; display: flex; flex-direction: column; }
        .input {
          padding: 10px 14px;
          border: 1px solid #ccc;
          border-radius: 8px;
          font-size: 16px;
        }
        .row { display: flex; gap: 20px; }
        .col { flex: 1; display: flex; flex-direction: column; }
      `}</style>
    </div>
  );
}
