import React, { useState, useEffect } from "react";
import api from "../api";

export default function Checkout() {

  const [items, setItems] = useState([]);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [street, setStreet] = useState("");
  const [number, setNumber] = useState("");
  const [reference, setReference] = useState("");
  const [cep, setCep] = useState("");
  const [region, setRegion] = useState("Jandira");
  const [frete, setFrete] = useState(10);

  useEffect(() => {
    loadCart();
  }, []);

  // Atualiza frete automaticamente quando muda a região
  useEffect(() => {
    if (region === "Jandira") setFrete(10);
    else setFrete(20);
  }, [region]);

  async function loadCart() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) return;

    const r = await api.get("/cart/" + sessionId);
    setItems(r.data);
  }

  let subtotal = items.reduce((acc, i) => acc + i.price * i.quantity, 0);
  let total = subtotal + frete;

  async function enviarPedido() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      alert("Sessão inválida.");
      return;
    }

    const enderecoCompleto =
      `${street}, Nº ${number}, ${region} - CEP ${cep}. Ref: ${reference}`;

    try {
      const r = await api.post("/orders/checkout", {
        sessionId,
        name,
        phone,
        address: enderecoCompleto
      });

      const pedido = r.data;

      // ==== MONTAR MENSAGEM PARA WHATSAPP ====
      let msg = `DEEPSURVEY SUPLEMENTOS\n`;
      msg += `------\n`;
      msg += `Pedido #${pedido.id}\n`;
      msg += `------\n\n`;

      msg += `Cliente: ${name}\n`;
      msg += `WhatsApp: ${phone}\n`;
      msg += `Endereço: ${enderecoCompleto}\n\n`;

      msg += `Produtos:\n`;
      pedido.items.forEach((it) => {
        msg += `- ${it.product.name} (Qtd: ${it.quantity}) - R$ ${it.price.toFixed(2)}\n`;
      });

      msg += `------\nSubtotal: R$ ${subtotal.toFixed(2)}\n`;
      msg += `Entrega: R$ ${frete.toFixed(2)}\n`;
      msg += `Total: R$ ${total.toFixed(2)}\n`;
      msg += `------\n`;
      msg += `https://deepsurveysuplementos.com.br`;

      const encoded = encodeURIComponent(msg);

      window.location.href =
        `https://wa.me/5511947935371?text=${encoded}`;

    } catch (e) {
      console.error(e);
      alert("Erro ao enviar pedido. Confira o console.");
    }
  }

  return (
    <div className="container" style={{ paddingTop: 30 }}>
      <h2>Pedido Delivery</h2>

      <div style={{ display: "flex", gap: 40 }}>

        {/* FORMULÁRIO */}
        <div style={{ flex: 1 }}>

          <h3>Dados do cliente</h3>

          <label>Nome completo</label>
          <input value={name} onChange={(e) => setName(e.target.value)} />

          <label>WhatsApp</label>
          <input value={phone} onChange={(e) => setPhone(e.target.value)} />

          <h3>Entrega</h3>

          <label>Bairro / Região</label>
          <select value={region} onChange={(e) => setRegion(e.target.value)}>
            <option>Jandira</option>
            <option>Barueri</option>
            <option>Cotia</option>
            <option>Itapevi</option>
          </select>

          <label>Rua</label>
          <input value={street} onChange={(e) => setStreet(e.target.value)} />

          <label>Número</label>
          <input value={number} onChange={(e) => setNumber(e.target.value)} />

          <label>CEP</label>
          <input value={cep} onChange={(e) => setCep(e.target.value)} />

          <label>Ponto de referência</label>
          <input value={reference} onChange={(e) => setReference(e.target.value)} />

          <h3>Pagamento</h3>
          <p>A forma de pagamento será definida na conversa.</p>

          <button
            style={{
              marginTop: 20,
              padding: "12px 20px",
              background: "#25D366",
              color: "white",
              border: "none",
              borderRadius: 8,
              fontSize: 16,
              cursor: "pointer"
            }}
            onClick={enviarPedido}
          >
            Enviar pedido (WhatsApp)
          </button>
        </div>

        {/* RESUMO */}
        <div
          style={{
            width: 320,
            padding: 20,
            background: "#fff8d5",
            borderRadius: 10,
            border: "1px solid #e6d28a"
          }}
        >
          <h3 style={{ textAlign: "center" }}>DeepSurvey Suplementos</h3>

          <h4>Pedido</h4>
          {items.map((i) => (
            <div key={i.id}>
              {i.product.name} <br />
              Qtd: {i.quantity} — R$ {i.price.toFixed(2)}
              <hr />
            </div>
          ))}

          <p>Subtotal: <b>R$ {subtotal.toFixed(2)}</b></p>
          <p>Entrega: <b>R$ {frete.toFixed(2)}</b></p>

          <hr />
          <p>Total: <b>R$ {total.toFixed(2)}</b></p>
        </div>
      </div>
    </div>
  );
}
