import React, { useEffect, useState } from "react";
import api from "../api";

export default function Checkout() {
  const [items, setItems] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const [delivery, setDelivery] = useState(0);
  const [total, setTotal] = useState(0);

  const [form, setForm] = useState({
    name: "",
    phone: "",
    region: "",
    cep: "",
    street: "",
    number: "",
    complement: "",
    reference: "",
    paymentMethod: "Dinheiro",
    change: ""
  });

  useEffect(() => {
    load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  async function load() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      setItems([]);
      setSubtotal(0);
      setTotal(0);
      setDelivery(0);
      return;
    }

    try {
      const r = await api.get("/cart/" + sessionId);
      const data = r.data || [];
      setItems(data);

      const st = data.reduce((acc, i) => acc + (i.price || 0) * (i.quantity || 0), 0);
      setSubtotal(st);

      // atualizar total com delivery atual (se já escolhido)
      setTotal(st + delivery);
    } catch (err) {
      console.error("Erro ao carregar carrinho", err);
      setItems([]);
      setSubtotal(0);
      setTotal(0);
    }
  }

  function calcDelivery(region) {
    const fees = {
      Jandira: 10,
      Barueri: 20,
      Itapevi: 20,
      Cotia: 20
    };
    const fee = fees[region] || 0;
    setDelivery(fee);
    setTotal(subtotal + fee);
  }

  async function submitOrder() {
    const sessionId = localStorage.getItem("sessionId");
    if (!sessionId) {
      alert("Sessão inválida. Adicione produtos ao carrinho primeiro.");
      return;
    }

    const req = {
      sessionId,
      ...form
    };

    try {
      const r = await api.post("/orders/checkout", req);
      const order = r.data;

      // montar endereço exibido na mensagem
      let enderecoCompleto =
        `${form.street || ""}${form.number ? ", " + form.number : ""}` +
        (form.complement ? ` – ${form.complement}` : "") +
        (form.region ? `, ${form.region}` : "") +
        (form.cep ? `, CEP: ${form.cep}` : "") +
        (form.reference ? ` (Ref: ${form.reference})` : "");

      // montar mensagem legível
      let msg =
        `*DEEPSURVEY SUPLEMENTOS*\n` +
        `------------------------------\n` +
        `*Pedido #${order.id}*\n` +
        `------------------------------\n\n` +
        `Itens do pedido:\n`;

      order.items.forEach(i => {
        msg += `• ${i.quantity}x ${i.product.name} – R$ ${Number(i.price).toFixed(2).replace(".", ",")}\n`;
      });

      msg +=
        `\n------------------------------\n` +
        `Subtotal: R$ ${subtotal.toFixed(2).replace(".", ",")}\n` +
        `Entrega: R$ ${delivery.toFixed(2).replace(".", ",")}\n` +
        `Total: R$ ${total.toFixed(2).replace(".", ",")}\n` +
        `------------------------------\n\n` +
        `Endereço Completo:\n${enderecoCompleto}\n\n` +
        `Forma de pagamento: ${form.paymentMethod}\n\n` +
        `www.deepsurveysuplementos.com.br`;

      const phone = "5511947935371";
      const url = `https://wa.me/${phone}?text=${encodeURIComponent(msg)}`;

      // redirecionar para o WhatsApp
      window.location.href = url;
    } catch (e) {
      console.error("Erro no checkout:", e);
      alert("Erro ao enviar pedido. Veja o console.");
    }
  }

  // helpers para exibir valores com formatação brasileira
  function fmt(v) {
    return Number(v || 0).toFixed(2).replace(".", ",");
  }

  return (
    <div className="container" style={{ maxWidth: 900, paddingTop: 30 }}>
      <h2>Pedido Delivery</h2>

      <div style={{ display: "flex", gap: 30, alignItems: "flex-start" }}>
        {/* ---------------- FORMULÁRIO ---------------- */}
        <div style={{ flex: 1 }}>
          <h4>Dados do cliente</h4>

          <input className="form-control" placeholder="Nome completo"
            onChange={e => setForm({ ...form, name: e.target.value })} />

          <input className="form-control" placeholder="WhatsApp (ex: 11912345678)"
            onChange={e => setForm({ ...form, phone: e.target.value })} />

          <h4 style={{ marginTop: 20 }}>Entrega</h4>

          <select className="form-control"
            onChange={e => {
              setForm({ ...form, region: e.target.value });
              calcDelivery(e.target.value);
            }}>
            <option value="">Selecione...</option>
            <option>Jandira</option>
            <option>Barueri</option>
            <option>Itapevi</option>
            <option>Cotia</option>
          </select>

          <input className="form-control" placeholder="CEP"
            onChange={e => setForm({ ...form, cep: e.target.value })} />

          <input className="form-control" placeholder="Rua"
            onChange={e => setForm({ ...form, street: e.target.value })} />

          <input className="form-control" placeholder="Número"
            onChange={e => setForm({ ...form, number: e.target.value })} />

          <input className="form-control" placeholder="Complemento"
            onChange={e => setForm({ ...form, complement: e.target.value })} />

          <input className="form-control" placeholder="Ponto de referência"
            onChange={e => setForm({ ...form, reference: e.target.value })} />

          <h4 style={{ marginTop: 20 }}>Pagamento</h4>

          <select className="form-control"
            onChange={e => setForm({ ...form, paymentMethod: e.target.value })}>
            <option>Dinheiro</option>
            <option>Cartão de Débito</option>
            <option>Cartão de Crédito</option>
            <option>Pix</option>
          </select>

          <input className="form-control" placeholder="Troco (opcional)"
            onChange={e => setForm({ ...form, change: e.target.value })} />

          <button className="btn btn-success mt-3" onClick={submitOrder}>
            Enviar pedido via WhatsApp
          </button>
        </div>

        {/* ---------------- RESUMO / NOTA (formato pedido) ---------------- */}
        <div style={{ width: 320 }}>
          <div style={{
            background: "#fff8d5",
            padding: 18,
            borderRadius: 6,
            boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
            border: "1px solid rgba(0,0,0,0.06)"
          }}>
            <div style={{ textAlign: "center", fontWeight: 700 }}>
              DEEPSURVEY SUPLEMENTOS
            </div>

            <div style={{ textAlign: "center", color: "#777", margin: "8px 0 12px" }}>
              <span style={{ display: "inline-block", width: 80, borderTop: "2px solid rgba(0,0,0,0.15)" }} />
            </div>

            <div style={{ textAlign: "center", fontWeight: 700, marginBottom: 6 }}>Pedido</div>

            <div style={{ height: 8 }} />

            <div style={{ borderTop: "1px dashed rgba(0,0,0,0.12)", paddingTop: 10 }}>
              <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 8 }}>
                <div style={{ color: "#333" }}>Subtotal:</div>
                <div style={{ fontWeight: 700 }}>R$ {fmt(subtotal)}</div>
              </div>

              <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 8 }}>
                <div style={{ color: "#333" }}>Entrega:</div>
                <div style={{ fontWeight: 700 }}>{delivery > 0 ? `R$ ${fmt(delivery)}` : "—"}</div>
              </div>

              <div style={{ borderTop: "1px solid rgba(0,0,0,0.06)", marginTop: 10, paddingTop: 10 }}>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <div style={{ fontWeight: 800 }}>Total:</div>
                  <div style={{ fontWeight: 800 }}>R$ {fmt(total)}</div>
                </div>
              </div>
            </div>

            <div style={{ marginTop: 14, textAlign: "center", color: "#333", fontSize: 13 }}>
              {form.paymentMethod ? (
                <>
                  <div style={{ fontWeight: 700, marginBottom: 6 }}>{form.paymentMethod}</div>
                </>
              ) : (
                <div style={{ color: "#999" }}>Forma de pagamento:</div>
              )}
            </div>

            <div style={{ marginTop: 14, textAlign: "center", color: "#666", fontSize: 13 }}>
              <a href="https://www.deepsurveysuplementos.com.br" target="_blank" rel="noreferrer"
                style={{ color: "#333", textDecoration: "none" }}>
                www.deepsurveysuplementos.com.br
              </a>
            </div>
          </div>

          <div style={{ textAlign: "center", color: "#888", marginTop: 12 }}>
            O seu pedido será enviado para o nosso WhatsApp
          </div>
        </div>
      </div>
    </div>
  );
}
