import React, { useEffect, useState } from "react";
import { useSearchParams, Link } from "react-router-dom";
import api from "../api";

export default function SearchResults() {
  const [params] = useSearchParams();
  const query = params.get("q") || "";
  const [products, setProducts] = useState([]);

  useEffect(() => {
    if (!query) return;
    async function load() {
      try {
        const r = await api.get("/products/search?q=" + query);
        setProducts(r.data);
      } catch (err) {
        console.error("Erro ao buscar:", err);
      }
    }
    load();
  }, [query]);

  return (
    <div className="container" style={{ paddingTop: 30 }}>
      <h2>Resultados para: <b>{query}</b></h2>

      {products.length === 0 && (
        <p style={{ marginTop: 20 }}>Nenhum produto encontrado.</p>
      )}

      <div style={{ display: "flex", flexWrap: "wrap", gap: 20 }}>
        {products.map((p) => (
          <Link
            key={p.id}
            to={`/produto/${p.id}`}
            className="card themed-box"
            style={{ width: 220 }}
          >
            <img
              src={
                p.imageUrl?.startsWith("http")
                  ? p.imageUrl
                  : process.env.PUBLIC_URL + "/images/" + p.imageUrl
              }
              alt={p.name}
              style={{
                width: "100%",
                height: 180,
                objectFit: "contain",
                borderRadius: 8,
                marginBottom: 10,
              }}
            />

            <h3>{p.name}</h3>

            {p.onSale ? (
              <>
                <p className="price-promo">
                  R$ {p.salePrice.toFixed(2).replace(".", ",")}
                </p>

                <p className="old-price">
                  R$ {p.price.toFixed(2).replace(".", ",")}
                </p>
              </>
            ) : (
              <p className="price">
                R$ {p.price.toFixed(2).replace(".", ",")}
              </p>
            )}
          </Link>
        ))}
      </div>
    </div>
  );
}
