import React, { useEffect, useState } from "react";
import api from "../api";

export default function Checkout() {
  const [items, setItems] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const [delivery, setDelivery] = useState(0);
  const [total, setTotal] = useState(0);

  const [form, setForm] = useState({
    name: "",
    phone: "",
    region: "",
    cep: "",
    street: "",
    number: "",
    complement: "",
    reference: "",
    paymentMethod: "Dinheiro",
    change: ""
  });

  useEffect(() => load(), []);

  async function load() {
    const sessionId = localStorage.getItem("sessionId");
    const r = await api.get("/cart/" + sessionId);
    setItems(r.data);

    const st = r.data.reduce((acc, i) => acc + i.price * i.quantity, 0);
    setSubtotal(st);
  }

  function calcDelivery(region) {
    const fees = {
      Jandira: 10,
      Barueri: 20,
      Itapevi: 20,
      Cotia: 20
    };
    const fee = fees[region] || 0;
    setDelivery(fee);
    setTotal(subtotal + fee);
  }

  async function submitOrder() {
    const sessionId = localStorage.getItem("sessionId");

    const req = {
      sessionId,
      ...form
    };

    try {
      const r = await api.post("/orders/checkout", req);
      const order = r.data;

      let msg =
        `*DEEPSURVEY SUPLEMENTOS*\n` +
        `------------------------------\n` +
        `*Pedido #${order.id}*\n\n` +
        `*Itens do pedido:*\n`;

      order.items.forEach(i => {
        msg += `• ${i.quantity}x ${i.product.name} — R$ ${i.price.toFixed(2)}\n`;
      });

      msg +=
        `\n------------------------------\n` +
        `Subtotal: R$ ${subtotal.toFixed(2)}\n` +
        `Entrega: R$ ${delivery.toFixed(2)}\n` +
        `Total: *R$ ${total.toFixed(2)}*\n` +
        `------------------------------\n\n` +
        `*Endereço Completo:*\n${order.address}\n\n` +
        `Forma de pagamento: ${form.paymentMethod}\n\n` +
        `https://deepsurveysuplementos.com.br`;

      const phone = "5511947935371";

      const url = `https://wa.me/${phone}?text=${encodeURIComponent(msg)}`;
      window.location.href = url;

    } catch (e) {
      alert("Erro ao enviar pedido. Veja o console.");
      console.error(e);
    }
  }

  return (
    <div className="container" style={{ maxWidth: 900, paddingTop: 20 }}>

      {/* ====================== TÍTULO ======================= */}
      <h2 className="mb-4">Pedido Delivery</h2>

      <div className="row">

        {/* ====================== FORMULÁRIO ======================= */}
        <div className="col-md-7">

          {/* ---------- Dados do cliente ---------- */}
          <h5 className="mt-3">
            <i className="bi bi-person"></i> Dados do cliente
          </h5>
          <hr />

          <label className="mt-2">Nome completo:</label>
          <input
            className="form-control"
            onChange={e => setForm({ ...form, name: e.target.value })}
          />

          <label className="mt-3">WhatsApp:</label>
          <input
            className="form-control"
            onChange={e => setForm({ ...form, phone: e.target.value })}
          />

          {/* ---------- Entrega ---------- */}
          <h5 className="mt-4">
            <i className="bi bi-truck"></i> Entrega
          </h5>
          <hr />

          <label>Bairro / Região:</label>
          <select
            className="form-control"
            onChange={e => {
              setForm({ ...form, region: e.target.value });
              calcDelivery(e.target.value);
            }}
          >
            <option value="">Selecione...</option>
            <option>Jandira</option>
            <option>Barueri</option>
            <option>Itapevi</option>
            <option>Cotia</option>
          </select>

          <div className="row mt-3">
            <div className="col">
              <label>CEP:</label>
              <input
                className="form-control"
                onChange={e => setForm({ ...form, cep: e.target.value })}
              />
            </div>
            <div className="col">
              <label>Nº:</label>
              <input
                className="form-control"
                onChange={e => setForm({ ...form, number: e.target.value })}
              />
            </div>
          </div>

          <label className="mt-3">Rua:</label>
          <input
            className="form-control"
            onChange={e => setForm({ ...form, street: e.target.value })}
          />

          <label className="mt-3">Complemento:</label>
          <input
            className="form-control"
            onChange={e => setForm({ ...form, complement: e.target.value })}
          />

          <label className="mt-3">Ponto de referência:</label>
          <input
            className="form-control"
            onChange={e => setForm({ ...form, reference: e.target.value })}
          />

          {/* ---------- Pagamento ---------- */}
          <h5 className="mt-4">
            <i className="bi bi-cash-stack"></i> Pagamento
          </h5>
          <hr />

          <label>Forma de pagamento:</label>
          <select
            className="form-control"
            onChange={e => setForm({ ...form, paymentMethod: e.target.value })}
          >
            <option>Dinheiro</option>
            <option>Cartão de Débito</option>
            <option>Cartão de Crédito</option>
            <option>Pix</option>
          </select>

          <label className="mt-3">Deseja troco?</label>
          <input
            className="form-control"
            placeholder="Deixe em branco se não precisar"
            onChange={e => setForm({ ...form, change: e.target.value })}
          />

          <button
            className="btn btn-success mt-4 w-100"
            onClick={submitOrder}
          >
            Enviar pedido via WhatsApp
          </button>
        </div>

        {/* ====================== RESUMO ======================= */}
        <div className="col-md-5">
          <div
            className="p-3 mt-3"
            style={{
              background: "#fff8d5",
              borderRadius: 10,
              border: "1px solid #f0e3a0",
            }}
          >
            <h5><i className="bi bi-receipt-cutoff"></i> Resumo do pedido</h5>
            <hr />

            <p><b>Subtotal:</b> R$ {subtotal.toFixed(2)}</p>
            <p><b>Entrega:</b> R$ {delivery.toFixed(2)}</p>

            <p className="mt-3">
              <b>Total: </b>
              <span style={{ fontSize: 20 }}>R$ {total.toFixed(2)}</span>
            </p>

            <hr />

            <p style={{ fontSize: 13, textAlign: "center" }}>
              O seu pedido será enviado para o nosso WhatsApp
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
